import React from 'react';
import { getSrcFromHtml } from '../../utils/helperz';
import data from '../../data/featured.json';

const FeaturedRow = () => {
    const handleLinkClick = (linkout, e) => {
        e.preventDefault();
        window.open(linkout, '_blank');
    };

    const ArticleList = () => {
        let componentList = [];
        data.map((article, idx) => {
            if (idx === 0 || idx > 5) return null;
            let currentObj = {
                title: article.title,
                categories: article.categories,
                link: article.link,
                content: article.contentSnippet
            }
            componentList.push(currentObj);
            return null;
        });

        return (
            componentList.map((article, i) => (
                <div
                    className='featured-article-list-item'
                    onClick={(e) => handleLinkClick(article.link, e)}
                    key={`featured-article-list-item-${i}`}
                >
                    <div className='article-title title-hover'>{article.title}</div>
                    <div className='keyword-link-container bottom-border'>
                        {article.categories.map((category, j) => (
                            <div className='keyword-link' key={`category-item-${j}`}>
                                <a
                                    className='category-item'
                                    href={article.link}
                                >
                                    <span>{category}</span>
                                </a>
                            </div>
                        ))}
                    </div>
                </div>
            ))
        )
    };

    return (
        <div className='feed-row featured-row-container'>
            <div className='featured-article-container' onClick={(e) => handleLinkClick(data[0].link, e)}>
                <div className='featured-article-title title-hover'>{data[0].title}</div>
                <img className='featured-article-img' src={getSrcFromHtml(data[0].content)} alt='featured-article-img' />
            </div>
            <div className='featured-article-list'>
                <ArticleList/>
            </div>
        </div>
    );
};

export default FeaturedRow;