import React from 'react';
import { getSrcFromHtml } from '../../utils/helperz';
import data from '../../data/credit.json';

const Credit = () => {
    const handleLinkClick = (linkout, e) => {
        e.preventDefault();
        window.open(linkout, '_blank');
    };

    return (
        <>
            <div className='row-column-item'>
                <img src={getSrcFromHtml(data[2].content, true, 'sm')} className='row-column-item-image' alt='img' />
                <div className='row-column-item-content'>
                    <div className='row-column-item-title title-hover' onClick={(e) => handleLinkClick(data[2].link, e)}>{data[2].title}</div>
                    <div className='keyword-link-container small-column-categories'>
                        {data[2].categories.map((category, j) => (
                            <div className='keyword-link' key={`category-item-${j}`}>
                                <a className='category-item' href={data[2].link}>
                                    <span>{category}</span>
                                </a>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
            <div className='row-column-item'>
                <img src={getSrcFromHtml(data[3].content, true, 'sm')} className='row-column-item-image' alt='img' />
                <div className='row-column-item-content'>
                    <div className='row-column-item-title title-hover' onClick={(e) => handleLinkClick(data[3].link, e)}>{data[3].title}</div>
                    <div className='keyword-link-container small-column-categories'>
                        {data[3].categories.map((category, j) => (
                            <div className='keyword-link' key={`category-item-${j}`}>
                                <a className='category-item' href={data[3].link}>
                                    <span>{category}</span>
                                </a>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </>
    )
}


export default Credit;